mapper.update:
UPDATE board
SET title = #{title}, contents = #{contents}, url = #{url}, created_at = now()
WHERE id = #{id}

mapper.select:
SELECT b.*
FROM board b




            JOIN ccachiuser cu ON b.user_id = cu.id
            WHERE cu.name = #{word}


            WHERE b.${key} LIKE CONCAT('%', #{word}, '%')






        ORDER BY b.${orderBy} ${orderByDir}


        ORDER BY b.created_at DESC;
